import React, {Component} from 'react'

class SearchBar extends Component {
    constructor(props) {
        super(props);

        this.handleSearchValueChange = this.handleSearchValueChange.bind(this);
        this.handleCheckboxValueChange = this.handleCheckboxValueChange.bind(this);
    }

    handleSearchValueChange(event) {
        this.props.handleSearchValueChange(event.target.value);
    }

    handleCheckboxValueChange(event) {
        this.props.handleCheckboxValueChange(event.target.value);
    }

    render() {
      return (
        <div>
          <form>
            <input type="text" 
            className="aaaa" 
            placeholder="Searching..."
            value={this.props.filterText} 
            onChange={this.handleSearchValueChange.bind(this)}/>
            <br/>
            <input type="checkbox" 
            value={this.props.inStockOnly}
            onChange={this.handleCheckboxValueChange.bind(this)} /> {' '}Only show products in stock111
          </form>
        </div>
      );
    }
  }

  class ProductTable extends Component {
      constructor(props) {
          super(props);
      }

      render() {
          return(
              <div>

              </div>
          );
      }
  }

  class FilterableProductTable extends Component {
    // eslint-disable-next-line no-useless-constructor
    constructor(props) {
      super(props);

      this.state = {filterText: '', inStockOnly: false};
      this.handleSearchValueChange = this.handleSearchValueChange.bind(this);
      this.handleCheckboxValueChange = this.handleCheckboxValueChange.bind(this);
    }

    handleSearchValueChange(value) {
        this.setState({filterText: value});
    }

    handleCheckboxValueChange(value) {
        this.setState({inStockOnly: value});
    }

    render() {
      return (
        <div>
          <SearchBar 
          filterText={this.state.filterText} 
          inStockOnly={this.state.inStockOnly}
          handleSearchValueChange={this.handleSearchValueChange.bind(this)}
          handleCheckboxValueChange={this.handleCheckboxValueChange.bind(this)} 
          />
        </div>
      );
    }
  }

  export default FilterableProductTable;
