import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class SearchBar extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div>
        <form>
          <input type="text" className="Input_Product" vlaue={this.props.filterText} />
          <br/>
          <input type="checkbox" value={this.props.inStockOnly}/> Only show products in stock
        </form>
      </div>
    );
  }
}

class FilterableProductTable extends Component {
  // eslint-disable-next-line no-useless-constructor
  constructor(props) {
    super(props);

    this.state = {filterText: 'ball', inStockOnly: true};
  }

  render() {
    return (
      <div>
        <SearchBar filterText={this.state.filterText} inStockOnly={this.state.inStockOnly}/>
      </div>
    );
  }
}

class App extends Component {
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
        <FilterableProductTable className="Filterable-Product"/>
      </div>
    );
  }
}

export default App;
