import React, {Component} from 'react';
import './FilterableProductTable.css';

class SearchBar extends Component {
    constructor(props) {
        super(props);

        this.handleFilterTextInputChange = this.handleFilterTextInputChange.bind(this);
        this.handleInStockInputChange = this.handleInStockInputChange.bind(this);
    }

    handleFilterTextInputChange(event) {
        this.props.onFilterTextInput(event.target.value);
    }

    handleInStockInputChange(event) {
        this.props.onInStockInput(event.target.checked);
    }

    render() {
      return (
        <div>
          <form>
            <input type="text" 
            className="aaaa" 
            placeholder="Searching..."
            value={this.props.filterText} 
            onChange={this.handleFilterTextInputChange.bind(this)}/>
            <p>
            <input type="checkbox" 
            checked={this.props.inStockOnly}
            onChange={this.handleInStockInputChange.bind(this)} /> {' '}Only show products in stock
            </p>
          </form>
        </div>
      );
    }
  }

  class ProductCategoryRow extends Component {
    render() {
        return (<tr><th colSpan="2">{this.props.category}</th></tr>);
      }
  }

  class ProductRow extends Component {
      render() {
        var name = this.props.product.stocked ?
        this.props.product.name :
        <span style={{color: 'red'}}>
          {this.props.product.name}
        </span>;
      return (
        <tr>
          <td>{name}</td>
          <td>{this.props.product.price}</td>
        </tr>
      );  
    }
  }
  class ProductTable extends Component {
      // eslint-disable-next-line no-useless-constructor
      constructor(props) {
          super(props);
      }

      render() {
        var rows = [];
        var lastCategory = null;
        this.props.products.forEach((product) => {
          if (product.name.indexOf(this.props.filterText) === -1 || (!product.stocked && this.props.inStockOnly)) {
            return;
          }
          if (product.category !== lastCategory) {
            rows.push(<ProductCategoryRow category={product.category} key={product.category} />);
          }
          rows.push(<ProductRow product={product} key={product.name} />);
          lastCategory = product.category;
        });
        return (
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Price</th>
              </tr>
            </thead>
            <tbody>{rows}</tbody>
          </table>
        );
      }
  }

  class FilterableProductTable extends Component {
    // eslint-disable-next-line no-useless-constructor
    constructor(props) {
      super(props);

      this.state = {filterText: '', inStockOnly: false};
      this.handleFilterTextInput = this.handleFilterTextInput.bind(this);
      this.handleInStockInput = this.handleInStockInput.bind(this);
    }

    handleFilterTextInput(filterText) {
        this.setState({filterText: filterText});
    }

    handleInStockInput(value) {
        this.setState({inStockOnly: value});
    }

    render() {
      return (
        <div>
          <SearchBar 
          filterText={this.state.filterText} 
          inStockOnly={this.state.inStockOnly}
          onFilterTextInput={this.handleFilterTextInput.bind(this)}
          onInStockInput={this.handleInStockInput.bind(this)} 
          />
          <ProductTable
          products={this.props.products}
          filterText={this.state.filterText}
          inStockOnly={this.state.inStockOnly}
        />
        </div>
      );
    }
  }

  export default FilterableProductTable;
